export interface EnhancedGridIconProps
  extends React.HTMLAttributes<HTMLDivElement> {
  type: string;
  color?: string;
}

export const Icon = ({
  type,
  color,
  className,
  ...props
}: EnhancedGridIconProps) => {
  return (
    <div
      style={{
        backgroundColor: color,
      }}
      className={cn(
        className,
        "w-10 h-10 flex items-center justify-center rounded-lg shadow-grid shrink-0"
      )}
      {...props}
    >
      {Icons[type]}
    </div>
  );
};
